import path from "node:path"
import { fileURLToPath } from "node:url"

export const onPreBuild = function () {
  const pluginBasePath = path.dirname(fileURLToPath(import.meta.url))

  return {
    generatedFunctions: [
      {
        path: path.resolve(pluginBasePath, "functions/plugin-func1.mjs")
      }
    ]
  }
}
