import { execa, execaCommand } from 'execa'

export const onPreBuild = async function () {
  console.log('!!!! inside onPreBuild')
  try {

    await execaCommand('which netlify-config', { stdio:'inherit' })
    await execa('netlify-config', ['--version'], { stdio:'inherit' })
  } catch(err) {
    console.log(err)
  }
}
